import styles from './ImageGrid.module.css';

import { Block } from '../../blocks';
import { TailSpin, FidgetSpinner } from "react-loader-spinner";
import { ClipLoader } from 'react-spinners';

type ImageGridProps = {
  data?: Block;
  isloading?: boolean;
};

export const ImageGrid = (props: ImageGridProps) => {
  return props.isloading ? (<div className={styles.spinner}><ClipLoader color="red" loading={props.isloading} /></div>) :
    (<div className={styles.imageGrid}>
      Replace me
    </div>
  )};
