import styles from './ImageGrid.module.css';

import { Block } from '../../blocks';
import { Hourglass } from 'react-loader-spinner';
import { getImageDetailsArray } from '../../utils';

type ImageGridProps = {
  data?: Block;
  isloading?: boolean;
};
export const ImageGrid = (props: ImageGridProps) => {
  let imageDetails: Block[] = [];
  if (!props.isloading && props.data) {
    imageDetails = getImageDetailsArray(props.data);
  }
  console.log(imageDetails);
  return (
    <div className={styles.imageGrid}>
      {props.isloading ? (
        <div className={styles.spinner} data-testid="loader">
          <Hourglass
            colors={['#0ACA87 ', '#0ACA87 ']}
            height="110"
            width="110"
          />
        </div>
      ) : (
        <div>
          {imageDetails.map((imageData, index) => (
            <img
              width = {385}
              style={{ padding: "15px"}}
              height={260}
              key={index}
              src={imageData.options?.url}
              alt={`Image ${index}`}
            />
          ))}
        </div>
      )}
    </div>
  );
};
